import React, { useState } from 'react';
var videoSrc = './video/moon.mp4';

const VideoPlayer = ({ videoSrc }) => {
  const [playing, setPlaying] = useState(false);

  const handlePlay = () => {
    setPlaying(true);
  };

  const handlePause = () => {
    setPlaying(false);
  };

  return (
    <div className="video-player">
      <video
        ref={(ref) => {
          this.videoRef = ref;
        }}
        src={videoSrc} {...playing ? 'autoPlay' : ''}
        controls
        onPlay={handlePlay}
        onPause={handlePause}
      >
        <track kind="captions" />
      </video>
    </div>
 );
};

export default VideoPlayer;
